mapper.select:
SELECT cc.ccSeq ,cc.ccName ,cc.ccGroupSeq as cgSeq ,cg.cgName ,cc.ccDelNy ,cc.ccUseNy ,cc.ccOrder FROM cc cc join ccg cg on cg.cgSeq = cc.ccGroupSeq WHERE 1=1 AND cc.ccDelNy = #{shDelNy}
AND cc.ccSeq = #{shValue} AND cc.ccName LIKE CONCAT('%',#{shValue},'%')
order by cc.ccSeq desc

    	SELECT COUNT(*)
    	FROM cc
   		WHERE 1=1
		AND ccDelNy = #{shDelNy}

			AND ccSeq = #{shValue}
			AND ccName LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
    	insert  into cc (
    		ccName
		    ,ccGroupSeq
		    ,ccDelNy
		    ,ccUseNy
		    ,ccOrder
    	)
    	values (
    		#{ccName}
		    ,#{ccGroupSeq}
		    ,#{ccDelNy}
		    ,#{ccUseNy}
		    ,#{ccOrder}
    	)

    		SELECT last_insert_id()

mapper.update:
    		update cc
    		set 
	    		ccName =#{ccName}
			    ,ccGroupSeq=#{ccGroupSeq}
			    ,ccDelNy=#{ccDelNy}
			    ,ccUseNy=#{ccUseNy}
			    ,ccOrder=#{ccOrder}
    		where ccSeq = #{ccSeq}
