mapper.select:
 	SELECT
 		*
		FROM member
		WHERE 1=1
		AND mmId = #{mmId}
		AND mmPassword = #{mmPassword}
		AND mmAdminNy = #{mmAdminNy}

mapper.update:
UPDATE member
SET
	mmDelNy = 0 
	,mmNickName = #{mmNickName}
	,mmEmail = #{mmEmail}
	,mmTelecom = #{mmTelecom}
	,mmPhone = #{mmPhone}
WHERE 1=1
AND mmSeq = #{mmSeq}

mapper.insert:
INSERT member
(
	mmId
	,mmName
	,mmNickName
	,mmPassword
	,mmGender
	,mmAge
	,mmDob
	,mmEmail
	,mmTelecom
	,mmPhone
)
VALUES
(
	#{mmId}
	,#{mmName}
	,#{mmNickName}
	,#{mmPassword}
	,#{mmGender} 
	,#{mmAge}
	,#{mmDob}
	,#{mmEmail}
	,#{mmTelecom}
	,#{mmPhone}
)

		SELECT last_insert_id()
