mapper.select:
SELECT COUNT(*)
FROM myVillage
WHERE 1=1
AND mvDelNy = #{shDelNy}

		AND mvMemberSeq = #{shValue}
		AND mvName LIKE CONCAT('%', #{shValue},'%')

mapper.insert:
INSERT INTO myVillage(
	mvName
	,mvOriginalAddr
	,mvMemberSeq
	,mvDefaultNy
)
VALUES(
	#{mvName}
	,#{mvOriginalAddr}
	,#{mvMemberSeq}

		,0
		,#{mvDefaultNy}

)

	SELECT last_insert_id()

mapper.update:
UPDATE myVillage
SET
	mvName = #{mvName}
	,mvMemberSeq = #{mvMemberSeq}
	,mvOriginalAddr =#{mvOriginalAddr}
	,mvDefaultNy

		=0
		=#{mvDefaultNy}

WHERE 
	mvSeq = #{mvSeq}
