mapper.insert:
    	INSERT INTO post (
    		poWriter
    		,poTitle
    		,poContent
    		,poCategory
    		,poLat
    		,poLng
    		,poAddr
    	)
    	VALUES (
    		#{poWriter}
    		,#{poTitle}
    		,#{poContent}
    		,#{poCategory}
    		,#{poLat}
    		,#{poLng}
    		,#{poAddr}
    		
    	)
	    
	    	SELECT last_insert_id()

mapper.update:
UPDATE post
SET
	poTitle = #{poTitle}
	,poContent = #{poContent}
	,poCategory = #{poCategory}
WHERE poSeq = #{poSeq}
