mapper.insert:
    	INSERT INTO post (
    		poWriter
    		,poTitle
    		,poContent
    		,poCategory
    		,poLng
    		,poLat
    		,poAddr
    	)
    	VALUES (
    		#{poWriter}
    		,#{poTitle}
    		,#{poContent}
    		,#{poCategory}
    		,#{poLng}
    		,#{poLat}
    		,#{poAddr}
    	)
	    
	    	SELECT last_insert_id()
